import{_ as n,o as r,c as i,a3 as t}from"./chunks/framework.DJT1Lw4G.js";const g=JSON.parse('{"title":"Einführung","description":"Einführung zum Werwolf Guide","frontmatter":{"layout":"home","lang":"de-CH","title":"Einführung","description":"Einführung zum Werwolf Guide","home":true,"hero":{"name":"Werwolf (Mafia) Guide","tagline":"Sammlung von Regeln, Rollen, Varianten, Tipps und mehr","actions":[{"theme":"brand","text":"Alle Regeln","link":"/regeln/"},{"theme":"alt","text":"Rollen","link":"/rollen/","type":"secondary"}]}},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),a={name:"index.md"};function l(o,e,s,f,d,h){return r(),i("div",{"data-pagefind-body":!0},e[0]||(e[0]=[t('<h2 id="einfuhrung" tabindex="-1">Einführung <a class="header-anchor" href="#einfuhrung" aria-label="Permalink to &quot;Einführung&quot;">​</a></h2><p>Dies ist eine Sammlung von <a href="/werewolf-guide/regeln/">Regeln</a> und <a href="/werewolf-guide/rollen/">Rollen</a> für das Social-Deduction-Spiel Werwolf (Mafia) wie ich und mein Umfeld es häufig spielen. Um das Spiel zu spielen, reicht ein Stift, Papier und ein wenig Vorbereitungszeit. Die Regeln, Varianten und Rollen sind so gewählt, dass auch grosse Runden gespielt werden können, viel Strategie möglich ist und es niemandem langweilig wird.</p><p>Das Spiel wurde von Dimma Davidoff in 1987 konzipiert<sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup> und in 1997 von Andrew Plotkin zum Werwolf-Stil angepasst<sup class="footnote-ref"><a href="#fn2" id="fnref2">[2]</a></sup>.<br> Zusätzliche Rollen und Regeln sind z.T. originell (meines Wissens), häufig aber auch von anderen Werwolf Versionen inspiriert wie <a href="https://en.wikipedia.org/wiki/Ultimate_Werewolf" target="_blank" rel="noreferrer">Ultimate Werewolf</a> (© Ted Alspach) und <a href="https://de.wikipedia.org/wiki/Die_Werw%C3%B6lfe_von_D%C3%BCsterwald" target="_blank" rel="noreferrer">Die Werwölfe von Düsterwald</a> (© Philippe des Phalliéres &amp; Hervé Marly). Da die Bilder und z.T. Formulierungen dieser z.T. kommerziellen Versionen nicht offen lizenziert aber urheberrechtlich geschützt sind, werden sie in diesem Guide nicht verwendet/gezeigt sondern dienen rein zur Inspiration.</p><p>Dieser Guide ist unter <a href="http://creativecommons.org/licenses/by-sa/4.0/" target="_blank" rel="noreferrer">CC BY-SA 4.0</a> lizenziert. Ausnahmen werden klar deklariert. Eine Liste von Inspirationen kann <a href="/werewolf-guide/inspirationen">hier</a> gefunden werden. Falls versehentlich Urheberrechts- oder Lizenzverletzungen begangen wurden, bitte ich Sie, mit mir in Kontakt zu treten.</p><hr><hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p><a href="https://web.archive.org/web/19990302082118/http://members.theglobe.com/mafia_rules/" target="_blank" rel="noreferrer">Dimma Davidoffs Blogeintrag</a> <a href="#fnref1" class="footnote-backref">↩︎</a></p></li><li id="fn2" class="footnote-item"><p><a href="https://www.eblong.com/zarf/werewolf.html" target="_blank" rel="noreferrer">Andrew Plotkins Blogeintrag</a> <a href="#fnref2" class="footnote-backref">↩︎</a></p></li></ol></section>',7)]))}const p=n(a,[["render",l]]);export{g as __pageData,p as default};
