import{_ as r,o as a,c as l,a3 as i}from"./chunks/framework.DfdLQFYj.js";const u=JSON.parse('{"title":"Inspirationen","description":"Inspirationen für den Werwolf Guide","frontmatter":{"lang":"de-CH","title":"Inspirationen","description":"Inspirationen für den Werwolf Guide"},"headers":[],"relativePath":"inspirations.md","filePath":"inspirations.md"}'),t={name:"inspirations.md"};function n(o,e,s,h,w,f){return a(),l("div",{"data-pagefind-body":!0},e[0]||(e[0]=[i('<h1 id="inspirationen" tabindex="-1">Inspirationen <a class="header-anchor" href="#inspirationen" aria-label="Permalink to &quot;Inspirationen&quot;">​</a></h1><h2 id="kommerzielle-werwolf-versionen" tabindex="-1">Kommerzielle Werwolf Versionen <a class="header-anchor" href="#kommerzielle-werwolf-versionen" aria-label="Permalink to &quot;Kommerzielle Werwolf Versionen&quot;">​</a></h2><h3 id="ultimate-werewolf-one-night-version" tabindex="-1">Ultimate Werewolf &amp; One Night Version <a class="header-anchor" href="#ultimate-werewolf-one-night-version" aria-label="Permalink to &quot;Ultimate Werewolf &amp; One Night Version&quot;">​</a></h3><p><a href="https://en.wikipedia.org/wiki/Ultimate_Werewolf" target="_blank" rel="noreferrer">Ultimate Werewolf</a> ist eine Werwolf Version erstellt von Ted Alspach und veröffentlicht von Bézier Games, Inc.<br> Das Spiel diente als Inspiration bei verschiedenen Karten. Dieser Guide stellt keine Reproduktion dar. Rollen zur Inspiration sind im <a href="https://board-games.fandom.com/wiki/Ultimate_Werewolf/Roles" target="_blank" rel="noreferrer">Board Games Wiki</a> aufgeführt.</p><p><a href="https://one-night.fandom.com/wiki/One_Night_Ultimate_Werewolf" target="_blank" rel="noreferrer">One Night Ultimate Werewolf</a> (Variante von Ultimate Werewolf) spielt sich deutlich anders als das hier beschriebene Werwolf, ist aber ein super Spiel für kleinere Gruppen bis 10 Personen. Es hat ebenfalls einige interessante Rollen, welche adaptiert werden könnten.</p><h3 id="die-werwolfe-von-dusterwald" tabindex="-1">Die Werwölfe von Düsterwald <a class="header-anchor" href="#die-werwolfe-von-dusterwald" aria-label="Permalink to &quot;Die Werwölfe von Düsterwald&quot;">​</a></h3><p><a href="https://de.wikipedia.org/wiki/Die_Werw%C3%B6lfe_von_D%C3%BCsterwald" target="_blank" rel="noreferrer">Die Werwölfe von Düsterwald</a> ist eine Werwolf Version erstellt von Philippe des Phallières und Hervé Marly, veröffentlicht von verschiedenen Verlagen wie Lui-même.<br> Das Spiel diente als Inspiration bei verschiedenen Karten und auch beim grundsätzlichen Ablauf des Spiels. Dieser Guide stellt keine Reproduktion dar.</p><p>Die Werwölfe von Düsterwald war die erste Werwolf Version, die ich kannte und ich habe sie schon viele Male gespielt. Ich habe zudem gute Erfahrungen mit den Karten vom &quot;Die Werwölfe von Düsterwald - Der Pakt&quot; Set gemacht, welche auch für die hier beschriebenen Regeln verwendet werden können (gewisse Karten werden umfunktioniert). Damit sich neue Spieler die Bilder nicht merken müssen, habe ich die Karten noch beschriftet.</p><h3 id="blood-on-the-clocktower" tabindex="-1">Blood On The Clocktower <a class="header-anchor" href="#blood-on-the-clocktower" aria-label="Permalink to &quot;Blood On The Clocktower&quot;">​</a></h3><p>Obwohl <a href="https://bloodontheclocktower.com/" target="_blank" rel="noreferrer">Blood On The Clocktower</a> ein eigenes komplexes Spiel mit einigen entscheidenden Unterschieden zu Werwolf / Mafia ist, setze ich es unter diese Rubrik, weil es ohne Zweifel von Werwolf inspiriert wurde. Anders als Werwolf und gerade &quot;meiner&quot; Version, die hier erklärt ist, hat bei BOTC die Spielleitung eine deutlich aktivere Rolle und entscheidet den Verlauf des Spiels mit. Dadurch kann zusätzliche Spannung und tiefere Narrative erzeugt werden, doch das Spiel verliert an Determinismus. Ich persönlich mag Werwolf mehr, weil alles was passiert klaren Regeln folgt, zurückführbar ist und viel Strategie erlaubt. Ebenfalls ist BOTC nur für kleinere Gruppen geeignet und benötigt zusätzliches Material.</p><h3 id="wolvesville" tabindex="-1">Wolvesville <a class="header-anchor" href="#wolvesville" aria-label="Permalink to &quot;Wolvesville&quot;">​</a></h3><p><a href="https://www.wolvesville.com/" target="_blank" rel="noreferrer">Wolvesville</a> ist eine online Werwolf Version, welche auch eine offline Narrator-App zur Verfügung stellt (leider alles proprietär und kostenpflichtig). Diese Version enthält ebenfalls viele interessante Rollen, die als Inspiration dienen, jedoch funktionieren viele davon nur online und/oder mit einer algorithmischen Spielleitung.</p><p>Hier einige relevante Links des Wikis.</p><ul><li><a href="https://wolvesville.fandom.com/wiki/Category:Roles" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li><li><a href="https://wolvesville.fandom.com/wiki/Category:Classic" target="_blank" rel="noreferrer">Zusätzliche Rollen (offline only)</a></li><li><a href="https://wolvesville.fandom.com/wiki/Wolvesville_Classic" target="_blank" rel="noreferrer">Narrator-App (kommerziell)</a></li></ul><h3 id="werewolf-by-stellar-factory" tabindex="-1">Werewolf by Stellar Factory <a class="header-anchor" href="#werewolf-by-stellar-factory" aria-label="Permalink to &quot;Werewolf by Stellar Factory&quot;">​</a></h3><p><a href="https://playwerewolf.co/" target="_blank" rel="noreferrer">Werewolf</a> ist soweit ich verstehe ein moderner Konkurrent zu &quot;Die Werwölfe von Düsterwald&quot;. Es bietet nur wenige zusätzliche Rollen, ist aber nennenswert.</p><h2 id="sonstige-internet-ressourcen" tabindex="-1">Sonstige Internet-Ressourcen <a class="header-anchor" href="#sonstige-internet-ressourcen" aria-label="Permalink to &quot;Sonstige Internet-Ressourcen&quot;">​</a></h2><h3 id="wikipedia" tabindex="-1">Wikipedia <a class="header-anchor" href="#wikipedia" aria-label="Permalink to &quot;Wikipedia&quot;">​</a></h3><ul><li>Enthält diverse Interessante Informationen zu Geschichte, Theorie, Regeln, Rollen und Research mit Quellen. Verschidene Sprachen enthalten verschiedene Infos.</li><li><a href="https://en.wikipedia.org/wiki/Mafia_(party_game)" target="_blank" rel="noreferrer">Englische Seite</a></li><li><a href="https://de.wikipedia.org/wiki/Mafia_(Gesellschaftsspiel)" target="_blank" rel="noreferrer">Deutsche Seite</a></li></ul><h3 id="werewolfevents-com" tabindex="-1"><a href="https://www.werewolfevents.com" target="_blank" rel="noreferrer">werewolfevents.com</a> <a class="header-anchor" href="#werewolfevents-com" aria-label="Permalink to &quot;[werewolfevents.com](https://www.werewolfevents.com)&quot;">​</a></h3><ul><li><a href="http://www.werewolfevents.com/the-characters/" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li><li><a href="http://www.werewolfevents.com/modguideprep/" target="_blank" rel="noreferrer">Kanadisches Format</a></li></ul><h3 id="loups-garous-en-ligne-com" tabindex="-1"><a href="https://loups-garous-en-ligne.com" target="_blank" rel="noreferrer">loups-garous-en-ligne.com</a> <a class="header-anchor" href="#loups-garous-en-ligne-com" aria-label="Permalink to &quot;[loups-garous-en-ligne.com](https://loups-garous-en-ligne.com)&quot;">​</a></h3><ul><li><a href="https://docs.google.com/document/u/2/d/e/2PACX-1vS0kH2pvEoQtKvmO4PDr8vvOOiKW7Rhgq1s3nDT4uhle2t-LMrxFD361NTRu0bt0zW_NA8q3x6Il3it/pub#kix.8ev76r23btyb" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li></ul><h3 id="werewolf-chat" tabindex="-1"><a href="https://werewolf.chat" target="_blank" rel="noreferrer">werewolf.chat</a> <a class="header-anchor" href="#werewolf-chat" aria-label="Permalink to &quot;[werewolf.chat](https://werewolf.chat)&quot;">​</a></h3><ul><li><a href="https://werewolf.chat/Roles" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li><li><a href="https://werewolf.chat/Game_modes" target="_blank" rel="noreferrer">Zusätzliche Spielmodi</a></li></ul><h3 id="werewolfvillage-wiki-github" tabindex="-1"><a href="https://github.com/werewolfvillage/wiki/wiki" target="_blank" rel="noreferrer">werewolfvillage/wiki (GitHub)</a> <a class="header-anchor" href="#werewolfvillage-wiki-github" aria-label="Permalink to &quot;[werewolfvillage/wiki (GitHub)](https://github.com/werewolfvillage/wiki/wiki)&quot;">​</a></h3><ul><li><a href="https://github.com/werewolfvillage/wiki/wiki/Wolverine" target="_blank" rel="noreferrer">Wolverine</a></li><li><a href="https://github.com/werewolfvillage/wiki/wiki/Leprechaun" target="_blank" rel="noreferrer">Glückspilz</a></li></ul><h3 id="werewolf-the-game-fandom-com" tabindex="-1"><a href="https://werewolf-the-game.fandom.com/" target="_blank" rel="noreferrer">werewolf-the-game.fandom.com</a> <a class="header-anchor" href="#werewolf-the-game-fandom-com" aria-label="Permalink to &quot;[werewolf-the-game.fandom.com](https://werewolf-the-game.fandom.com/)&quot;">​</a></h3><ul><li><a href="https://werewolf-the-game.fandom.com/wiki/List_of_Roles" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li></ul><h3 id="wired-co-uk" tabindex="-1">wired.co.uk <a class="header-anchor" href="#wired-co-uk" aria-label="Permalink to &quot;wired.co.uk&quot;">​</a></h3><ul><li><a href="https://www.wired.co.uk/article/werewolf-extra-materials" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li></ul><h3 id="werewolv-es" tabindex="-1"><a href="https://werewolv.es/guides" target="_blank" rel="noreferrer">werewolv.es</a> <a class="header-anchor" href="#werewolv-es" aria-label="Permalink to &quot;[werewolv.es](https://werewolv.es/guides)&quot;">​</a></h3><ul><li><a href="https://werewolv.es/guides" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li><li><a href="https://github.com/werewolv-es/how-to-play" target="_blank" rel="noreferrer">GitHub Repo</a></li></ul><h3 id="linglp-github-io-playwerewolf" tabindex="-1"><a href="https://linglp.github.io/playwerewolf/" target="_blank" rel="noreferrer">linglp.github.io/playwerewolf</a> <a class="header-anchor" href="#linglp-github-io-playwerewolf" aria-label="Permalink to &quot;[linglp.github.io/playwerewolf](https://linglp.github.io/playwerewolf/)&quot;">​</a></h3><ul><li><a href="https://linglp.github.io/playwerewolf/" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li><li><a href="https://github.com/linglp/playwerewolf" target="_blank" rel="noreferrer">GitHub Repo</a></li></ul><h3 id="mafia-gamekeller-net" tabindex="-1"><a href="https://mafia.gamekeller.net" target="_blank" rel="noreferrer">mafia.gamekeller.net</a> <a class="header-anchor" href="#mafia-gamekeller-net" aria-label="Permalink to &quot;[mafia.gamekeller.net](https://mafia.gamekeller.net)&quot;">​</a></h3><ul><li><a href="https://mafia.gamekeller.net/roles/" target="_blank" rel="noreferrer">Zusätzliche Rollen</a></li><li><a href="https://github.com/gamekeller/mafia" target="_blank" rel="noreferrer">GitHub Repo</a></li></ul><h3 id="mafia-brotherhood-of-princeton-university" tabindex="-1"><a href="https://web.archive.org/web/20050829053528/http://www.princeton.edu/~mafia/mafia.htm" target="_blank" rel="noreferrer">Mafia Brotherhood of Princeton University</a> <a class="header-anchor" href="#mafia-brotherhood-of-princeton-university" aria-label="Permalink to &quot;[Mafia Brotherhood of Princeton University](https://web.archive.org/web/20050829053528/http://www.princeton.edu/~mafia/mafia.htm)&quot;">​</a></h3><ul><li><a href="https://web.archive.org/web/20050901044147/http://www.princeton.edu/~mafia/rules.htm" target="_blank" rel="noreferrer">Regeln &amp; Rollen</a></li><li><a href="https://web.archive.org/web/20051210143114/http://www.princeton.edu/~mafia/history.htm" target="_blank" rel="noreferrer">Geschichte von Mafia</a></li><li><a href="https://web.archive.org/web/20051218094519/http://www.princeton.edu/~mafia/links.htm" target="_blank" rel="noreferrer">Links &amp; Referenzen</a></li></ul>',39)]))}const c=r(t,[["render",n]]);export{u as __pageData,c as default};
